$(function(){$(".delete").click(function(){Swal.fire({title:"Na pewno chcesz usunąć?",text:"Operacja nie jest przywracalna!",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",cancelButtonText:"Zostaw",confirmButtonText:"Tak, usuń!"}).then(n=>{n.isConfirmed&&$.ajax({method:"DELETE",url:deleteURL+$(this).data("id")}).done(function(e){window.location.reload()}).fail(function(e){Swal.fire("OOPs",e.responseJSON.message,e.responseJSON.status)})})})});
