$(function(){$("div.products-count a").click(function(t){t.preventDefault(),$("a.products-actual-count").text($(this).text()),e($(this).text())}),$("a#filter-button").click(function(t){t.preventDefault(),e($("a.products-actual-count").first().text())}),$("button.add-cart-button").click(function(t){t.preventDefault(),$.ajax({method:"POST",url:WELCOME_DATA.addToCart+$(this).data("id")}).done(function(){Swal.fire({title:"Brawo!",text:"Produkt dodany do koszyka!",icon:"success",showCancelButton:!0,confirmButtonText:'<i class="fas fa-cart-plus"></i> Przejdź do koszyka',cancelButtonText:'<i class="fas fa-shopping-bag"></i> Kontynuuj zakupy'}).then(i=>{i.isConfirmed&&(window.location=WELCOME_DATA.listCart)})}).fail(function(){Swal.fire("Oops...","Wystąpił błąd","error")})});function e(t){const i=$("form.sidebar-filter").serialize();$.ajax({method:"GET",url:"/",data:i+"&"+$.param({paginate:t})}).done(function(o){$("div#products-wrapper").empty(),$.each(o.data,function(d,a){const s='<div class="col-6 col-md-6 col-lg-4 mb-3">            <div class="card h-100 border-0">                <div class="card-img-top">                    <img src="'+c(a)+'" class="img-fluid mx-auto d-block" alt="Zdjęcie produktu">                </div>                <div class="card-body text-center">                    <h4 class="card-title">'+a.name+'                    </h4>                    <h5 class="card-price small">                        <i>PLN '+a.price+'</i>                    </h5>                </div>                <button class="btn btn-success btn-sm add-cart-button"'+n()+' data-id="'+a.id+'">                   <i class="fas fa-cart-plus"></i> Dodaj do koszyka                </button>            </div>        </div>';$("div#products-wrapper").append(s)})})}function c(t){return t.image_path?WELCOME_DATA.storagePath+t.image_path:WELCOME_DATA.defaultImage}function n(){return WELCOME_DATA.isGuest?" disabled":""}});
